INSERT INTO destinations (slug, name, name_hi, headline, headline_hi, description, description_hi, icon, services, highlights, image_url, display_order, is_featured, is_published) VALUES
('delhi-ncr', 'Delhi NCR', 'दिल्ली एनसीआर', 'Business Hub of India', 'भारत का व्यापार केंद्र', 'Modern luxury meets ancient heritage in India''s capital region. Experience world-class business infrastructure alongside iconic monuments.', 'भारत की राजधानी क्षेत्र में आधुनिक विलासिता प्राचीन विरासत से मिलती है।', 'Building', ARRAY['Business facilities', 'Urban luxury hotels', 'Cultural experiences', 'Fine dining', 'Shopping'], ARRAY['Iconic monuments and museums', 'World-class business infrastructure', 'Michelin-starred restaurants', 'Luxury shopping malls'], '/placeholder.svg?height=600&width=800', 1, true, true),
('mumbai', 'Mumbai', 'मुंबई', 'Cosmopolitan Coastal Paradise', 'महानगरीय तटीय स्वर्ग', 'Bollywood glamour meets corporate sophistication in India''s financial capital. Stunning sea views and vibrant nightlife await.', 'भारत की वित्तीय राजधानी में बॉलीवुड ग्लैमर कॉर्पोरेट परिष्कार से मिलता है।', 'Building2', ARRAY['Coastal villas', 'Fine dining', 'Entertainment', 'Shopping', 'Business'], ARRAY['Gateway of India views', 'Luxury beachfront properties', 'Five-star dining experiences', 'Entertainment & nightlife'], '/placeholder.svg?height=600&width=800', 2, true, true),
('goa', 'Goa', 'गोवा', 'Tropical Leisure Escape', 'उष्णकटिबंधीय अवकाश पलायन', 'Sun, sand, and serenity in India''s premier beach destination. Perfect blend of Portuguese heritage and Indian hospitality.', 'भारत के प्रमुख समुद्र तट गंतव्य में सूर्य, रेत और शांति।', 'Palmtree', ARRAY['Luxury villas', 'Wellness retreats', 'Water sports', 'Fine dining', 'Events'], ARRAY['Private beach access', 'Wellness and yoga programs', 'Yacht charters', 'Sunset dining experiences'], '/placeholder.svg?height=600&width=800', 3, true, true),
('jaipur', 'Jaipur', 'जयपुर', 'The Pink City Experience', 'गुलाबी शहर का अनुभव', 'Royal heritage and cultural immersion in Rajasthan. Stay in palace hotels and experience Maharaja-level hospitality.', 'राजस्थान में शाही विरासत और सांस्कृतिक विसर्जन।', 'Crown', ARRAY['Heritage tours', 'Royal experiences', 'Cultural immersion', 'Fine dining', 'Events'], ARRAY['City Palace tours', 'Amber Fort experiences', 'Traditional Rajasthani cuisine', 'Cultural performances'], '/placeholder.svg?height=600&width=800', 4, true, true),
('varanasi', 'Varanasi', 'वाराणसी', 'Spiritual Awakening', 'आध्यात्मिक जागृति', 'The world''s oldest living city and holiest Hindu pilgrimage site. Experience the spiritual heart of India.', 'दुनिया का सबसे पुराना जीवित शहर और सबसे पवित्र हिंदू तीर्थ स्थल।', 'Sparkles', ARRAY['Spiritual concierge', 'Private ghat access', 'Cultural tours', 'Meditation', 'Wellness'], ARRAY['Private ghat experiences', 'Spiritual guidance', 'Traditional ceremonies', 'Cultural education'], '/placeholder.svg?height=600&width=800', 5, false, true),
('rishikesh', 'Rishikesh', 'ऋषिकेश', 'Yoga & Wellness Capital', 'योग और कल्याण राजधानी', 'Gateway to the Himalayas and center of spiritual wellness. World-class yoga and meditation experiences.', 'हिमालय का प्रवेश द्वार और आध्यात्मिक कल्याण का केंद्र।', 'Heart', ARRAY['Yoga retreats', 'Wellness programs', 'Adventure activities', 'Meditation', 'Ayurveda'], ARRAY['World-class yoga programs', 'Ayurvedic treatments', 'Adventure activities', 'Meditation & wellness'], '/placeholder.svg?height=600&width=800', 6, false, true),
('south-india', 'South India', 'दक्षिण भारत', 'Tropical Splendor', 'उष्णकटिबंधीय भव्यता', 'Backwaters, beaches, and ancient temples across Kerala, Tamil Nadu, and beyond. Explore India''s tropical paradise.', 'केरल, तमिलनाडु और उससे परे बैकवाटर, समुद्र तट और प्राचीन मंदिर।', 'Waves', ARRAY['Backwater cruises', 'Temple tours', 'Beach resorts', 'Spice route', 'Ayurveda'], ARRAY['Backwater houseboats', 'Ancient temple tours', 'Spice plantation visits', 'Traditional cuisine'], '/placeholder.svg?height=600&width=800', 7, false, true);
