INSERT INTO testimonials (client_name, client_title, client_company, client_location, client_image_url, platform, platform_url, rating, content, content_hi, service_type, destination, is_featured, display_order, is_published) VALUES
('Takeshi Yamamoto', 'Managing Director', 'Toyota Motor Corporation', 'Tokyo, Japan', '/placeholder.svg?height=100&width=100', 'google', 'https://g.co/kgs/sewa-hospitality', 5, 'SEWA made our executive relocation seamless. From visa processing to finding the perfect residence in Gurgaon, their attention to detail is unmatched. Highly recommend for any Japanese company entering India.', 'SEWA ने हमारे कार्यकारी स्थानांतरण को निर्बाध बना दिया। वीजा प्रोसेसिंग से लेकर गुड़गांव में परफेक्ट आवास खोजने तक, उनका विस्तार पर ध्यान बेजोड़ है।', 'relocation', 'delhi-ncr', true, 1, true),
('Sarah Mitchell', 'Expat Spouse', NULL, 'London, United Kingdom', '/placeholder.svg?height=100&width=100', 'trustpilot', 'https://www.trustpilot.com/review/sewa-hospitality', 5, 'Moving to Delhi with two children was daunting until SEWA stepped in. They handled school admissions, found us a beautiful home, and even arranged cultural orientation. Truly exceptional service that made India feel like home.', 'दो बच्चों के साथ दिल्ली जाना डराने वाला था जब तक SEWA ने कदम नहीं रखा। उन्होंने स्कूल एडमिशन संभाला, हमें एक सुंदर घर दिलाया।', 'relocation', 'delhi-ncr', true, 2, true),
('Hans Mueller', 'CEO', 'Mueller Engineering GmbH', 'Munich, Germany', '/placeholder.svg?height=100&width=100', 'direct', NULL, 5, 'Our corporate retreat in Rajasthan was perfectly orchestrated by SEWA. From private palace dinners to heritage tours, every moment was memorable. Professional, discrete, and absolutely reliable - exactly what we needed.', 'राजस्थान में हमारी कॉर्पोरेट रिट्रीट SEWA द्वारा पूर्ण रूप से आयोजित की गई थी।', 'experiences', 'jaipur', true, 3, true),
('Jennifer Park', 'Travel Blogger', 'Wanderlust Chronicles', 'Seoul, South Korea', '/placeholder.svg?height=100&width=100', 'tripadvisor', 'https://www.tripadvisor.com/sewa-hospitality', 5, 'SEWA curated an incredible 2-week luxury journey through Kerala and Goa. The backwater houseboat experience and beachfront villa were beyond expectations. True luxury travel with authentic Indian experiences.', 'SEWA ने केरल और गोवा के माध्यम से एक अविश्वसनीय 2-सप्ताह की लक्जरी यात्रा तैयार की।', 'travel', 'south-india', true, 4, true),
('Mohammed Al-Rashid', 'Investment Banker', 'Dubai Holdings', 'Dubai, UAE', '/placeholder.svg?height=100&width=100', 'google', 'https://g.co/kgs/sewa-hospitality', 5, 'SEWA''s 24/7 concierge service has been invaluable during my frequent visits to Mumbai. From last-minute restaurant reservations to airport VIP services, they never disappoint. Essential for any serious business traveler.', 'मुंबई की मेरी बार-बार की यात्राओं के दौरान SEWA की 24/7 कन्सीयज सेवा अमूल्य रही है।', 'concierge', 'mumbai', true, 5, true),
('Lisa Chen', 'Marketing Director', 'TechVentures Singapore', 'Singapore', '/placeholder.svg?height=100&width=100', 'direct', NULL, 5, 'Organizing our company offsite in Goa was completely stress-free with SEWA. They managed luxury accommodations, team-building activities, and even arranged a special beach dinner. Will definitely use again for our next corporate event.', 'गोवा में हमारी कंपनी ऑफसाइट का आयोजन SEWA के साथ पूरी तरह से तनाव-मुक्त था।', 'corporate', 'goa', true, 6, true),
('Pierre Dubois', 'Diplomat', 'French Embassy', 'Paris, France', '/placeholder.svg?height=100&width=100', 'trustpilot', 'https://www.trustpilot.com/review/sewa-hospitality', 5, 'As a diplomat with complex protocol requirements, SEWA understood our needs perfectly. Their discretion, professionalism, and attention to security details made our delegation''s visit to India flawless.', 'जटिल प्रोटोकॉल आवश्यकताओं वाले एक राजनयिक के रूप में, SEWA ने हमारी जरूरतों को पूरी तरह से समझा।', 'concierge', 'delhi-ncr', false, 7, true),
('Yuki Nakamura', 'Family Traveler', NULL, 'Osaka, Japan', '/placeholder.svg?height=100&width=100', 'tripadvisor', 'https://www.tripadvisor.com/sewa-hospitality', 5, 'Traveling through India with elderly parents and young children seemed impossible until SEWA made it magical. Every hotel, every transfer, every experience was perfectly tailored to our family''s needs.', 'बुजुर्ग माता-पिता और छोटे बच्चों के साथ भारत में यात्रा करना असंभव लग रहा था जब तक SEWA ने इसे जादुई नहीं बना दिया।', 'travel', 'jaipur', false, 8, true);
